import { UserType } from './../Types/Types';
import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { useDispatch } from 'react-redux';
import axios from 'axios';


export const mainPageSlice = createSlice({
  name: 'mainPage',
  initialState: {
    usersCards: null as null | Array<UserType>
  },
  reducers: {
    addToUsersCards: (state, action) => {
      state.usersCards = action.payload
    },
  },
})

const GetUsersCards = createAsyncThunk(
    'users/GetUsersCards', 

    async (userId, thunkAPI) => {

      const response = await axios.get('http://localhost:8001/tinder/cards')
    addToUsersCards('ds')
    }
  )
// Action creators are generated for each case reducer function
export const { addToUsersCards } = mainPageSlice.actions

export default mainPageSlice.reducer
